import {config} from "@onflow/config"

config()
  .put("accessNode.api", "http://localhost:8080")
  .put("challenge.handshake", "http://localhost:8700/authenticate")
import {config} from "@onflow/config"
import {resolve} from "@onflow/sdk"

config().put("sdk.resolve", resolve)
export const VERSION = "0.0.71"
import {invariant} from "@onflow/util-invariant"
import * as sdk from "@onflow/sdk"
// import {send, script, decode, limit, args, arg} from "@onflow/sdk"
import * as t from "@onflow/types"

const isRequired = d => typeof d != null
const isString = d => typeof d === "string"
const isFunc = d => typeof d === "function"

/** Query the Flow Blockchain
 *
 *  @arg {Object} opts         - Query Options and configuration
 *  @arg {string} opts.cadence - Cadence Script used to query Flow
 *  @arg {ArgsFn} opts.args    - Arguments passed to cadence script
 *  @arg {number} opts.limit   - Compute Limit for Query
 *  @returns {Promise<Response>}
 *
 *  Where:
 *    @callback ArgsFn
 *    @arg {ArgFn}  arg - Argument function to define a single argument
 *    @arg {Object} t   - Cadence Types object used to define the type
 *    @returns {args[]}
 *
 *    @callback ArgFn
 *    @arg {Any}  value - the value of the argument
 *    @arg {Type} type  - the cadence type of the value
 *    @returns {arg}
 *
 *  Example:
 *    const cadence = `
 *      cadence: `
 *        pub fun main(a: Int, b: Int, c: Address): Int {
 *          log(c)
 *          return a + b
 *        }
 *    `.trim()
 *
 *    const args = (arg, t) => [
 *      arg(5, t.Int),
 *      arg(7, t.Int),
 *      arg("0xb2db43ad6bc345fec9", t.Address),
 *    ]
 *
 *    await query({ cadence, args })
 */
export async function query(opts = {}) {
  await preQuery(opts)

  // prettier-ignore
  return sdk.send([
    sdk.script(opts.cadence),
    sdk.args(normalizeArgs(opts.args || [])),
    opts.limit && typeof opts.limit === "number" && sdk.limit(opts.limit)
  ]).then(sdk.decode)
}

async function preQuery(opts) {
  invariant(
    isRequired(opts.cadence),
    "query({ cadence }) -- cadence is required"
  )

  invariant(
    isString(opts.cadence),
    "query({ cadence }) -- cadence must be a string"
  )
}

function normalizeArgs(ax) {
  if (isFunc(ax)) return ax(sdk.arg, t)
  return []
}
import {interaction, pipe} from "@onflow/sdk"
import {resolve as defaultResolve} from "@onflow/sdk"
import {createSignableVoucher} from "@onflow/sdk"
import {config} from "@onflow/config"

export const serialize = async (args = [], opts = {}) => {
  // prettier-ignore
  const resolveFunction = await config()
    .get("sdk.resolve", opts.resolve || defaultResolve(opts))

  if (Array.isArray(args)) args = await pipe(interaction(), args)

  return JSON.stringify(
    createSignableVoucher(await resolveFunction(args)),
    null,
    2
  )
}
import "../default-config"
import {config} from "@onflow/config"
import {
  spawn,
  send,
  subscriber,
  snapshoter,
  UPDATED,
  SNAPSHOT,
  INIT,
  SUBSCRIBE,
  UNSUBSCRIBE,
} from "@onflow/util-actor"
import {send as fclSend} from "@onflow/sdk"
import {decode} from "@onflow/sdk"
import {getTransactionStatus} from "@onflow/sdk"

const RATE = 2500
const POLL = "POLL"

const fetchTxStatus = async (transactionId) => {
  return fclSend([getTransactionStatus(transactionId)]).then(decode)
}

const isExpired = (tx) => tx.status === 5
const isSealed = (tx) => tx.status >= 4
const isExecuted = (tx) => tx.status >= 3
const isFinalized = (tx) => tx.status >= 2
const isPending = (tx) => tx.status >= 1
const isUnknown = (tx) => tx.status >= 0

const isDiff = (cur, next) => {
  return JSON.stringify(cur) !== JSON.stringify(next)
}

const HANDLERS = {
  [INIT]: async (ctx) => {
    const tx = await fetchTxStatus(ctx.self())
    if (!isSealed(tx)) setTimeout(() => ctx.sendSelf(POLL), RATE)
    ctx.merge(tx)
  },
  [SUBSCRIBE]: (ctx, letter) => {
    ctx.subscribe(letter.from)
    ctx.send(letter.from, UPDATED, ctx.all())
  },
  [UNSUBSCRIBE]: (ctx, letter) => {
    ctx.unsubscribe(letter.from)
  },
  [SNAPSHOT]: async (ctx, letter) => {
    letter.reply(ctx.all())
  },
  [POLL]: async (ctx) => {
    const tx = await fetchTxStatus(ctx.self())
    if (!isSealed(tx)) setTimeout(() => ctx.sendSelf(POLL), RATE)
    if (isDiff(ctx.all(), tx)) ctx.broadcast(UPDATED, tx)
    ctx.merge(tx)
  },
}

const scoped = (transactionId) => {
  if (typeof transactionId === "object")
    transactionId = transactionId.transactionId
  if (transactionId == null) throw new Error("transactionId required")
  return transactionId
}

const spawnTransaction = (transactionId) => {
  return spawn(HANDLERS, scoped(transactionId))
}

export function transaction(transactionId) {
  function snapshot() {
    return snapshoter(transactionId, spawnTransaction)
  }

  function subscribe(callback) {
    return subscriber(scoped(transactionId), spawnTransaction, callback)
  }

  function once(predicate) {
    return function innerOnce(opts = {}) {
      const suppress = opts.suppress || false
      return new Promise((resolve, reject) => {
        const unsub = subscribe((txStatus) => {
